import React, { useEffect, useState } from 'react';
import apiClient from '../apiClient';
import { generateToken, setToken } from '../authUtils';

interface User {
  ID_pers: number;
  name: string;
  firstname: string;
}

const UserSelect: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedUser, setSelectedUser] = useState<string>('');

  useEffect(() => {
    apiClient.get('/api/persons')
      .then((data) => {
        setUsers(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  const handleUserChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const userId = parseInt(event.target.value, 10);
    setSelectedUser(event.target.value);

    if (!isNaN(userId)) {
      const token = generateToken(userId);
      setToken(token);
    }
  };

  if (loading) {
    return <div>Loading users...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <label htmlFor="user-select">Select User: </label>
      <select id="user-select" value={selectedUser} onChange={handleUserChange}>
        <option value="">--Please choose a user--</option>
        {users.map((user) => (
          <option key={user.ID_pers} value={user.ID_pers}>
            {user.name} {user.firstname}
          </option>
        ))}
      </select>
      {selectedUser && <p>Selected: {selectedUser}</p>}
    </div>
  );
};

export default UserSelect;
