import React, { useEffect, useState } from 'react';
import { API_BASE_URL } from '../apiConfig';
import './Show.css';

interface DataItem {
  ID_pers: number;
  name: string;
  firstname: string;
  subject: string;
  comment?: string;
  month: string;
  load: number;
}

interface GroupedData {
  name: string;
  firstname: string;
  subject: string;
  comment: string;
  loads: { [key: string]: number };
}

function Show() {
  const [data, setData] = useState<DataItem[]>([]);
  const [groupedData, setGroupedData] = useState<GroupedData[]>([]);
  const [months, setMonths] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [sortConfig, setSortConfig] = useState<{ key: string, direction: string } | null>(null);

  useEffect(() => {
    fetch(`${API_BASE_URL}/api/data`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setData(data);
        processData(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  const processData = (data: DataItem[]) => {
    const grouped: { [key: string]: GroupedData } = {};
    const monthsSet = new Set<string>();

    data.forEach((item) => {
      const key = `${item.name}-${item.firstname}-${item.subject}-${item.comment || 'No comment'}`;
      monthsSet.add(item.month);

      if (!grouped[key]) {
        grouped[key] = {
          name: item.name,
          firstname: item.firstname,
          subject: item.subject,
          comment: item.comment || 'No comment',
          loads: {},
        };
      }

      grouped[key].loads[item.month] = item.load;
    });

    setGroupedData(Object.values(grouped));
    setMonths(Array.from(monthsSet).sort());
  };

  const requestSort = (key: string) => {
    let direction = 'ascending';
    if (sortConfig && sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  const sortedData = React.useMemo(() => {
    if (!sortConfig) {
      return groupedData;
    }
    return [...groupedData].sort((a, b) => {
      if (sortConfig.key === 'name') {
        if (a.name < b.name) {
          return sortConfig.direction === 'ascending' ? -1 : 1;
        }
        if (a.name > b.name) {
          return sortConfig.direction === 'ascending' ? 1 : -1;
        }
      } else if (sortConfig.key === 'subject') {
        if (a.subject < b.subject) {
          return sortConfig.direction === 'ascending' ? -1 : 1;
        }
        if (a.subject > b.subject) {
          return sortConfig.direction === 'ascending' ? 1 : -1;
        }
      }
      return 0;
    });
  }, [groupedData, sortConfig]);

  const calculateSubtotals = (data: GroupedData[], key: string) => {
    const subtotals: { [key: string]: number } = {};

    data.forEach(item => {
      const value = key === 'name' ? item.name : item.subject;
      if (!subtotals[value]) {
        subtotals[value] = 0;
      }

      Object.values(item.loads).forEach(load => {
        subtotals[value] += load;
      });
    });

    return subtotals;
  };

  const subtotals = React.useMemo(() => {
    if (!sortConfig) {
      return {};
    }
    return calculateSubtotals(sortedData, sortConfig.key);
  }, [sortedData, sortConfig]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h1>Show Page</h1>
      <table>
        <thead>
          <tr>
            <th onClick={() => requestSort('name')}>Name</th>
            <th>Firstname</th>
            <th onClick={() => requestSort('subject')}>Subject</th>
            <th>Comment</th>
            {months.map((month) => {
  const date = new Date(month);
  const monthStr = String(date.getMonth() + 1).padStart(2, '0');
  const yearStr = String(date.getFullYear()).slice(-2);
  return <th key={month}>{`${monthStr}\n${yearStr}`}</th>;
})}
          </tr>
        </thead>
        <tbody>
          {sortedData.map((item, index) => (
            <tr key={index}>
              <td>{item.name}</td>
              <td>{item.firstname}</td>
              <td>{item.subject}</td>
              <td>{item.comment}</td>
              {months.map((month) => (
                <td key={month}>{item.loads[month] || 0}</td>
              ))}
            </tr>
          ))}

          {sortConfig && (
            <React.Fragment>
              {Object.keys(subtotals).map((key, index) => (
                <tr key={`subtotal-${index}`}>
                  <td colSpan={sortConfig.key === 'name' ? 1 : 4} style={{ fontWeight: 'bold' }}>
                    {sortConfig.key === 'name' ? key : ''}
                  </td>
                  <td colSpan={sortConfig.key === 'name' ? 3 : 1} style={{ fontWeight: 'bold' }}>
                    {sortConfig.key === 'subject' ? key : ''}
                  </td>
                  {months.map((month, monthIndex) => (
                    <td key={`subtotal-${index}-${monthIndex}`} style={{ fontWeight: 'bold' }}>
                      {subtotals[key]}
                    </td>
                  ))}
                </tr>
              ))}
            </React.Fragment>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default Show;
