import React, { useEffect, useState, useContext, createContext } from 'react';
import { API_BASE_URL } from '../apiConfig';

interface User {
  id_pers: number;
  name: string;
  firstname: string;
}

interface UserSelectContextType {
  selectedUserId: number | null;
  setSelectedUserId: (id: number | null) => void;
}

const UserSelectContext = createContext<UserSelectContextType>({
  selectedUserId: null,
  setSelectedUserId: () => {},
});

export const useUserSelect = () => useContext(UserSelectContext);

const UserSelect: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedUser, setSelectedUser] = useState<string>('');
  const [token, setToken] = useState<string | null>(null);
  const { setSelectedUserId } = useUserSelect();

  useEffect(() => {
    fetch(`${API_BASE_URL}/api/persons`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setUsers(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  const handleUserChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const selectedUser = users.find(
      (user) => `${user.name} ${user.firstname}` === event.target.value
    );

    if (selectedUser) {
      fetch(`${API_BASE_URL}/api/generate-token`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId: selectedUser.id_pers }),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error(`Network response was not ok Buddy: ${JSON.stringify(selectedUser.id_pers)}`);
          }
          return response.json();
        })
        .then((data) => {
          setToken(data.token);
          localStorage.setItem('jwtToken', data.token);
          setSelectedUser(event.target.value);
          setSelectedUserId(selectedUser.id_pers);
        })
        .catch((error) => {
          setError(error.message);
        });
    } else {
      setSelectedUser(event.target.value);
      setSelectedUserId(null);
    }
  };

  if (loading) {
    return <div>Loading users...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <label htmlFor="user-select">Select User: </label>
      <select id="user-select" value={selectedUser} onChange={handleUserChange}>
        <option value="">--Please choose a user--</option>
        {users.map((user) => (
          <option key={user.id_pers} value={`${user.name} ${user.firstname}`}>
            {user.name} {user.firstname}
          </option>
        ))}
      </select>
      {selectedUser && <p>Selected: {selectedUser}</p>}
      {token && <p>Token: {token}</p>}
    </div>
  );
};

const UserSelectProvider: React.FC = ({ children }) => {
  const [selectedUserId, setSelectedUserId] = useState<number | null>(null);

  return (
    <UserSelectContext.Provider value={{ selectedUserId, setSelectedUserId }}>
      {children}
    </UserSelectContext.Provider>
  );
};

const UserSelectWithProvider: React.FC = () => (
  <UserSelectProvider>
    <UserSelect />
  </UserSelectProvider>
);

export default UserSelectWithProvider;
