import React, { useState } from 'react';
import { API_BASE_URL } from '../apiConfig';

const AdminManageImport: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [message, setMessage] = useState<string>('');
  const [isLoading, setIsLoading] = useState<boolean>(false);

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      setFile(e.target.files[0]);
    }
  };

  const handleImport = async () => {
    if (!file) {
      setMessage('Please select a CSV file first.');
      return;
    }

    setIsLoading(true);
    setMessage('');

    const token = localStorage.getItem('jwtToken');
    if (!token) {
      setMessage('Authentication error. Please log in again.');
      setIsLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch(`${API_BASE_URL}/api/import-subjects`, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      if (response.ok) {
        const result = await response.json();
        setMessage(`Successfully imported ${result.count} subjects.`);
      } else {
        const error = await response.json();
        setMessage(`Error importing subjects: ${error.message}`);
      }
    } catch (error) {
      setMessage(`Error importing subjects: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="import-container">
      <h2>Import Subjects from CSV</h2>
      <div className="import-content">
        <p>Upload a CSV file with "subject;type" format (one per line).</p>

        <div className="file-upload">
          <input
            type="file"
            accept=".csv"
            onChange={handleFileChange}
            disabled={isLoading}
          />
          {file && <p>Selected file: {file.name}</p>}
        </div>

        <button
          onClick={handleImport}
          disabled={!file || isLoading}
        >
          {isLoading ? 'Importing...' : 'Import Subjects'}
        </button>

        {message && <p className={message.includes('Error') ? 'error' : 'success'}>{message}</p>}
      </div>
    </div>
  );
};

export default AdminManageImport;
