// Modif.tsx
import React, { useEffect, useState, useCallback, useMemo } from 'react';
import { API_BASE_URL } from '../apiConfig';
import colorMapping from '../colorMapping.json';
import './Modif.css';

interface DataItem {
  id_pers: number;
  id_subject: number;
  name: string;
  firstname: string;
  subject: string;
  type: string;
  comment?: string;
  month: string; // e.g. "2024-08-01" or "2024-08-31" depending source
  load: number;
  team: string;
  color_hex?: string;
}

interface GroupedData {
  id_pers: number;
  id_subject: number;
  name: string;
  firstname: string;
  subject: string;
  type: string;
  comment: string;
  loads: Record<string, number>;
  team: string;
  color_hex?: string;
}

export default function Modif() {
  const [data, setData] = useState<DataItem[]>([]);
  const [groupedData, setGroupedData] = useState<GroupedData[]>([]);
  const [months, setMonths] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const [userId, setUserId] = useState('');
  const [filters, setFilters] = useState({ name: '', subject: '', team: '', minMonth: '', maxMonth: '' });
  const [sortConfig, setSortConfig] = useState<{ key: string; direction: 'asc' | 'desc' }>({ key: 'name', direction: 'asc' });

  // Editing cell: store month as the month string used in months array
  const [editingCell, setEditingCell] = useState<{ id_pers: number; id_subject: number; month: string } | null>(null);
  const [draftLoad, setDraftLoad] = useState<number | null>(null);

  // Editing comment
  const [editingComment, setEditingComment] = useState<{ id_pers: number; id_subject: number } | null>(null);
  const [draftComment, setDraftComment] = useState<string | null>(null);

  // ---------- helper to include token ----------
  const getAuthHeaders = () => {
    const token = localStorage.getItem('jwtToken');
    const headers: Record<string,string> = { 'Content-Type': 'application/json' };
    if (token) headers['Authorization'] = `Bearer ${token}`;
    return headers;
  };

  // ---------- load user id from token ----------
  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    if (token) {
      try {
        const decoded = JSON.parse(atob(token.split('.')[1]));
        setUserId(decoded.userId || '');
        document.title = `Modif - User: ${decoded.userId || ''}`;
      } catch (e) {
        setUserId('');
        document.title = 'Modif';
      }
    } else {
      document.title = 'Modif';
    }
  }, []);

  // ---------- fetch data ----------
  useEffect(() => {
    let mounted = true;
    async function fetchAll() {
      try {
        // Fetch related persons
        const token = localStorage.getItem('jwtToken');
        const parsed = token ? JSON.parse(atob(token.split('.')[1])) : { userId: '' };
        const uid = parsed.userId || '';

        const rel = await fetch(`${API_BASE_URL}/api/related-persons?id_pers=${uid}`, { headers: getAuthHeaders() });
        if (!rel.ok) throw new Error('Failed fetching related persons');
        const relJson = await rel.json();
        const relIds = relJson.map((r: any) => r.id_pers);

        // Fetch data
        const res = await fetch(`${API_BASE_URL}/api/data`, { headers: getAuthHeaders() });
        if (!res.ok) throw new Error('Failed fetching data');
        const allData: DataItem[] = await res.json();

        const filtered = allData.filter(item => relIds.includes(item.id_pers));
        if (!mounted) return;
        setData(filtered);
        processData(filtered);
        setLoading(false);
      } catch (e: any) {
        if (!mounted) return;
        setError(e.message || 'Erreur lors du chargement');
        setLoading(false);
        console.error(e);
      }
    }
    fetchAll();
    return () => { mounted = false; };
  }, []);

  // ---------- group data ----------
  const processData = useCallback((rows: DataItem[]) => {
    const grouped: Record<string, GroupedData> = {};
    const monthsSet = new Set<string>();

    rows.forEach(item => {
      const key = `${item.id_pers}-${item.id_subject}`;
      monthsSet.add(item.month);

      if (!grouped[key]) {
        grouped[key] = {
          id_pers: item.id_pers,
          id_subject: item.id_subject,
          name: item.name,
          firstname: item.firstname,
          subject: item.subject,
          type: item.type,
          comment: item.comment ?? 'No comment',
          loads: {},
          team: item.team ?? 'Unknown',
          color_hex: item.color_hex,
        };
      }

      grouped[key].loads[item.month] = item.load;
    });

    setGroupedData(Object.values(grouped));
    setMonths(Array.from(monthsSet).sort());
  }, []);

  // ---------- filtering & sorting ----------
  const filteredSortedData = useMemo(() => {
    let rows = [...groupedData];
    if (filters.name) rows = rows.filter(r => r.name.toLowerCase().includes(filters.name.toLowerCase()));
    if (filters.subject) rows = rows.filter(r => r.subject.toLowerCase().includes(filters.subject.toLowerCase()));
    if (filters.team) rows = rows.filter(r => r.team.toLowerCase().includes(filters.team.toLowerCase()));

    rows.sort((a, b) => {
      const vA: any = a[sortConfig.key as keyof GroupedData] ?? '';
      const vB: any = b[sortConfig.key as keyof GroupedData] ?? '';
      if (vA < vB) return sortConfig.direction === 'asc' ? -1 : 1;
      if (vA > vB) return sortConfig.direction === 'asc' ? 1 : -1;
      return 0;
    });

    return rows;
  }, [groupedData, filters, sortConfig]);

  // ---------- color helpers (kept similar to ton original) ----------
  const getBackgroundColor = (load: number) => {
    const keys = Object.keys(colorMapping).map(k => parseInt(k, 10)).sort((a,b)=>a-b);
    const values = Object.values(colorMapping);
    if (load <= keys[0]) return values[0];
    if (load >= keys[keys.length - 1]) return values[values.length - 1];

    for (let i = 0; i < keys.length - 1; i++) {
      const k1 = keys[i], k2 = keys[i+1];
      if (load >= k1 && load < k2) {
        const ratio = (load - k1) / (k2 - k1);
        const c1 = parseInt(values[i].substring(1), 16);
        const c2 = parseInt(values[i+1].substring(1), 16);
        const r = Math.round((((c2 >> 16) & 0xff) * ratio + ((c1 >> 16) & 0xff) * (1 - ratio)));
        const g = Math.round((((c2 >> 8) & 0xff) * ratio + ((c1 >> 8) & 0xff) * (1 - ratio)));
        const b = Math.round(((c2 & 0xff) * ratio + (c1 & 0xff) * (1 - ratio)));
        return `#${(r << 16 | g << 8 | b).toString(16).padStart(6, '0')}`;
      }
    }
    return values[0];
  };

  const getTextColor = (bg: string) => {
    const r = parseInt(bg.substring(1,3), 16);
    const g = parseInt(bg.substring(3,5), 16);
    const b = parseInt(bg.substring(5,7), 16);
    const luminance = (0.299*r + 0.587*g + 0.114*b)/255;
    return luminance > 0.5 ? '#000' : '#fff';
  };

  // ---------- API update helpers (optimistic) ----------
  async function updateLoadApi(payload: { id_pers: number; id_subject: number; month: string; load: number }) {
    const res = await fetch(`${API_BASE_URL}/api/submit`, {
      method: 'POST',
      headers: getAuthHeaders(),
      body: JSON.stringify(payload),
    });
    if (!res.ok) {
      const txt = await res.text();
      throw new Error(txt || 'Failed to update load');
    }
    return res.json();
  }

  async function updateCommentApi(payload: { id_pers: number; id_subject: number; comment: string }) {
    const res = await fetch(`${API_BASE_URL}/api/updateComment`, {
      method: 'POST',
      headers: getAuthHeaders(),
      body: JSON.stringify(payload),
    });
    if (!res.ok) {
      const txt = await res.text();
      throw new Error(txt || 'Failed to update comment');
    }
    return res.json();
  }

  // ---------- UI events: cell click -> start editing (or save currently edited one) ----------
  const startEditingCell = (id_pers: number, id_subject: number, month: string, currentLoad: number) => {
    // If another cell editing -> attempt save first (synchronously)
    if (editingCell && (editingCell.id_pers !== id_pers || editingCell.id_subject !== id_subject || editingCell.month !== month)) {
      // We call save directly and ignore errors (user will see alert if fails)
      // But for simplicity: save currently edited cell before switching
      saveEditingCell().catch(err => {
        console.error('Save failed while switching cell', err);
        alert('Erreur lors de la sauvegarde précédente : ' + err.message);
      });
    }
    setEditingCell({ id_pers, id_subject, month });
    setDraftLoad(currentLoad);
  };

  const saveEditingCell = async () => {
    if (!editingCell) return;
    const { id_pers, id_subject, month } = editingCell;
    if (draftLoad === null) { setEditingCell(null); return; }

    // optimistic update in local state
    const prevData = data.map(d => ({ ...d }));
    const newData = prevData.map(d => {
      if (d.id_pers === id_pers && d.id_subject === id_subject && d.month === month) {
        return { ...d, load: draftLoad };
      }
      // if row for that month doesn't exist, we can add a new item
      return d;
    });

    // If there wasn't a data item for that month, add
    const exists = prevData.some(d => d.id_pers === id_pers && d.id_subject === id_subject && d.month === month);
    if (!exists) {
      newData.push({
        id_pers,
        id_subject,
        name: groupNameFromIds(id_pers, id_subject) ?? '',
        firstname: '',
        subject: '',
        type: '',
        comment: '',
        month,
        load: draftLoad,
        team: '',
      } as DataItem);
    }

    // Apply optimistic update
    setData(newData);
    processData(newData); // regroup

    // Call API
    try {
      await updateLoadApi({ id_pers, id_subject, month, load: draftLoad });
      setEditingCell(null);
      setDraftLoad(null);
    } catch (err: any) {
      // revert on error
      setData(prevData);
      processData(prevData);
      console.error('Update load failed', err);
      alert('Erreur lors de la sauvegarde : ' + (err.message || err));
    }
  };

  // helper to find a name for optimistic new row (best-effort)
  const groupNameFromIds = (id_p: number, id_s: number) => {
    const found = data.find(d => d.id_pers === id_p && d.id_subject === id_s);
    if (found) return `${found.name}`;
    return '';
  };

  const cancelEditingCell = () => {
    setEditingCell(null);
    setDraftLoad(null);
  };

  // ---------- COMMENT edit handlers ----------
  const startEditingComment = (id_pers: number, id_subject: number, currentComment: string) => {
    if (editingComment && (editingComment.id_pers !== id_pers || editingComment.id_subject !== id_subject)) {
      // save previous comment before switching
      saveEditingComment().catch(err => {
        console.error('Saving previous comment failed', err);
        alert('Erreur sauvegarde commentaire : ' + err.message);
      });
    }
    setEditingComment({ id_pers, id_subject });
    setDraftComment(currentComment ?? '');
  };

  const saveEditingComment = async () => {
    if (!editingComment) return;
    const { id_pers, id_subject } = editingComment;
    if (draftComment === null) { setEditingComment(null); return; }

    // optimistic update in groupedData
    const prevData = data.map(d => ({ ...d }));
    const newData = prevData.map(d => {
      if (d.id_pers === id_pers && d.id_subject === id_subject) {
        return { ...d, comment: draftComment };
      }
      return d;
    });
    setData(newData);
    processData(newData);

    try {
      await updateCommentApi({ id_pers, id_subject, comment: draftComment });
      setEditingComment(null);
      setDraftComment(null);
    } catch (err: any) {
      setData(prevData);
      processData(prevData);
      console.error('Update comment failed', err);
      alert('Erreur lors de la sauvegarde du commentaire : ' + (err.message || err));
    }
  };

  const cancelEditingComment = () => {
    setEditingComment(null);
    setDraftComment(null);
  };

  // ---------- keyboard handlers ----------
  const onCellKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.currentTarget.blur(); // blur triggers save via onBlur below
      saveEditingCell().catch(err => console.error(err));
    } else if (e.key === 'Escape') {
      cancelEditingCell();
    }
  };

  const onCommentKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.currentTarget.blur();
      saveEditingComment().catch(err => console.error(err));
    } else if (e.key === 'Escape') {
      cancelEditingComment();
    }
  };

  // ---------- render ----------
  if (loading) return <div>Chargement…</div>;
  if (error) return <div>Erreur : {error}</div>;

  return (
    <div className="modif-page">
      <h1>Modif — User: {userId}</h1>

      <div className="filter-inputs">
        <input placeholder="Filter by Name" value={filters.name} onChange={(e) => setFilters({ ...filters, name: e.target.value })} />
        <input placeholder="Filter by Subject" value={filters.subject} onChange={(e) => setFilters({ ...filters, subject: e.target.value })} />
        <input placeholder="Filter by Team" value={filters.team} onChange={(e) => setFilters({ ...filters, team: e.target.value })} />
      </div>

      <table className="thin-bordered-table">
        <thead>
          <tr>
            <th onClick={() => setSortConfig({ key: 'team', direction: sortConfig.direction === 'asc' ? 'desc' : 'asc' })}>Team</th>
            <th onClick={() => setSortConfig({ key: 'name', direction: sortConfig.direction === 'asc' ? 'desc' : 'asc' })}>Name</th>
            <th onClick={() => setSortConfig({ key: 'firstname', direction: sortConfig.direction === 'asc' ? 'desc' : 'asc' })}>Firstname</th>
            <th onClick={() => setSortConfig({ key: 'subject', direction: sortConfig.direction === 'asc' ? 'desc' : 'asc' })}>Subject</th>
            <th onClick={() => setSortConfig({ key: 'type', direction: sortConfig.direction === 'asc' ? 'desc' : 'asc' })}>Type</th>
            <th>Comment</th>
            {months.map(month => {
              const d = new Date(month);
              const label = `${(d.getMonth()+1).toString().padStart(2,'0')} ${d.getFullYear().toString().slice(-2)}`;
              return <th key={month}>{label}</th>;
            })}
          </tr>
        </thead>
        <tbody>
          {filteredSortedData.map(item => (
            <tr key={`${item.id_pers}-${item.id_subject}`} style={{ backgroundColor: item.color_hex || undefined }}>
              <td>{item.team}</td>
              <td>{item.name}</td>
              <td>{item.firstname}</td>
              <td>{item.subject}</td>
              <td>{item.type}</td>
              <td style={{ fontStyle: 'italic' }}>
                {editingComment?.id_pers === item.id_pers && editingComment?.id_subject === item.id_subject ? (
                  <input
                    value={draftComment ?? item.comment}
                    onChange={(e) => setDraftComment(e.target.value)}
                    onBlur={() => saveEditingComment().catch(err => console.error(err))}
                    onKeyDown={onCommentKeyDown}
                    autoFocus
                  />
                ) : (
                  <span onClick={() => startEditingComment(item.id_pers, item.id_subject, item.comment)} style={{ cursor: 'pointer' }}>
                    {item.comment}
                  </span>
                )}
              </td>

              {months.map(month => {
                const load = item.loads[month] ?? 0;
                const isEditing = editingCell && editingCell.id_pers === item.id_pers && editingCell.id_subject === item.id_subject && editingCell.month === month;
                const bg = getBackgroundColor(load);
                const color = getTextColor(bg);
                const cellStyle: React.CSSProperties = { backgroundColor: bg, color, textAlign: 'center', padding: '6px', minWidth: '60px' };

                if (isEditing) {
                  return (
                    <td key={month} style={cellStyle}>
                      <input
                        type="number"
                        min={0}
                        max={31}
                        value={draftLoad ?? load}
                        onChange={(e) => setDraftLoad(Number(e.target.value))}
                        onBlur={() => saveEditingCell().catch(err => console.error(err))}
                        onKeyDown={onCellKeyDown}
                        autoFocus
                        style={{ width: '60px' }}
                      />
                    </td>
                  );
                }

                return (
                  <td key={month} style={cellStyle} onClick={() => startEditingCell(item.id_pers, item.id_subject, month, load)}>
                    {load}
                  </td>
                );
              })}
            </tr>
          ))}

          {/* Totals per person */}
          {Array.from(new Set(filteredSortedData.map(i => `${i.name} ${i.firstname}`))).map((name, idx) => {
            const rows = filteredSortedData.filter(i => `${i.name} ${i.firstname}` === name);
            const totals = months.map(m => rows.reduce((s, r) => s + (r.loads[m] ?? 0), 0));
            return (
              <tr key={`total-${idx}`}>
                <td colSpan={6} style={{ fontWeight: 'bold', backgroundColor: '#f0f0f0' }}>Total for {name}</td>
                {totals.map((t, i) => {
                  const bg = getBackgroundColor(t);
                  const color = getTextColor(bg);
                  return <td key={i} style={{ backgroundColor: bg, color, fontWeight: 'bold', textAlign: 'center' }}>{t}</td>;
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
