import React, { useEffect, useState } from 'react';
import { API_BASE_URL } from '../apiConfig';
import { isoToMMYY } from '../utils/dateUtils';
import './Admin_manage_debug.css';

function AdminManageDebug() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/data`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const result = await response.json();
        setData(result);
      } catch (error) {
        if (error instanceof Error) {
          setError(error.message);
        } else {
          setError('An unknown error occurred');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h2>Debug Menu</h2>
      <h3>Data from API</h3>
      <table className="debug-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Firstname</th>
            <th>Subject</th>
            <th>Type</th>
            <th>Comment</th>
            <th>Month</th>
            <th>Formatted month</th>
            <th>Load</th>
            <th>Color</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item: any) => (
            <tr key={item.id_pers + item.id_subject + item.month}>
              <td>{item.id_pers}</td>
              <td>{item.name}</td>
              <td>{item.firstname}</td>
              <td>{item.subject}</td>
              <td>{item.type}</td>
              <td>{item.comment || ''}</td>
              <td>{item.month}</td>
              <td>{isoToMMYY(item.month)}</td>
              <td>{item.load}</td>
              <td className="color-cell" style={{ backgroundColor: item.color_hex }}>
                {item.color_hex}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminManageDebug;
