import React from 'react';
import { API_BASE_URL } from '../apiConfig';

interface Subject {
  id_subject: number;
  subject: string;
  id_subject_type: number;
  type?: string;
}

interface SubjectType {
  id_subject_type: number;
  type: string;
  color_hex: string;
}

interface AdminManageSubjectsProps {
  subjects: Subject[];
  setSubjects: React.Dispatch<React.SetStateAction<Subject[]>>;
  subjectTypes: SubjectType[];
  newSubject: string;
  setNewSubject: React.Dispatch<React.SetStateAction<string>>;
  selectedSubjectType: number | null;
  setSelectedSubjectType: React.Dispatch<React.SetStateAction<number | null>>;
}

const AdminManageSubjects: React.FC<AdminManageSubjectsProps> = ({
  subjects,
  setSubjects,
  subjectTypes,
  newSubject,
  setNewSubject,
  selectedSubjectType,
  setSelectedSubjectType
}) => {

  const handleDeleteSubject = (id: number, subject: string) => {
    const isConfirmed = window.confirm(`Are you sure you want to delete the subject "${subject}"?`);
    if (isConfirmed) {
      const token = localStorage.getItem('jwtToken');
      if (token) {
        fetch(`${API_BASE_URL}/api/subjects/${id}`, {
          method: 'DELETE',
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
          .then(response => {
            if (response.ok) {
              setSubjects(subjects.filter(s => s.id_subject !== id));
            } else {
              // No alert for error
            }
          })
          .catch(error => {
            console.error('Error deleting subject:', error);
            // No alert for error
          });
      }
    }
  };

  return (
    <div className="subject-container">
      <div className="subjects-section">
        <h2>Subjects</h2>
        <div className="subjects-content">
          <div className="subjects-list">
            <table>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Subject</th>
                  <th>Type</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {subjects
                  .sort((a, b) => a.subject.localeCompare(b.subject))
                  .map(subject => (
                    <tr key={subject.id_subject}>
                      <td>{subject.id_subject}</td>
                      <td
                        contentEditable
                        suppressContentEditableWarning
                        onBlur={(e) => {
                          const newSubjectName = e.currentTarget.textContent?.trim() || '';
                          if (newSubjectName !== subject.subject) {
                            const token = localStorage.getItem('jwtToken');
                            if (token) {
                              fetch(`${API_BASE_URL}/api/subjects/${subject.id_subject}`, {
                                method: 'PUT',
                                headers: {
                                  'Content-Type': 'application/json',
                                  Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ subject: newSubjectName }),
                              })
                                .then(response => {
                                  if (response.ok) {
                                    // Update the subject name in the state
                                    setSubjects(prevSubjects =>
                                      prevSubjects.map(s =>
                                        s.id_subject === subject.id_subject
                                          ? { ...s, subject: newSubjectName }
                                          : s
                                      )
                                    );
                                  } else {
                                    // No alert for error
                                  }
                                })
                                .catch(error => {
                                  console.error('Error updating subject:', error);
                                  // No alert for error
                                });
                            }
                          }
                        }}
                      >
                        {subject.subject}
                      </td>
                      <td>
                        <select
                          value={subject.id_subject_type}
                          onChange={(e) => {
                            const newSubjectType = Number(e.target.value);
                            if (newSubjectType !== subject.id_subject_type) {
                              const token = localStorage.getItem('jwtToken');
                              if (token) {
                                fetch(`${API_BASE_URL}/api/subjects/${subject.id_subject}`, {
                                  method: 'PUT',
                                  headers: {
                                    'Content-Type': 'application/json',
                                    Authorization: `Bearer ${token}`,
                                  },
                                  body: JSON.stringify({ id_subject_type: newSubjectType }),
                                })
                                  .then(response => {
                                    if (response.ok) {
                                      // Update the subject type in the state
                                      setSubjects(prevSubjects =>
                                        prevSubjects.map(s =>
                                          s.id_subject === subject.id_subject
                                            ? { ...s, id_subject_type: newSubjectType }
                                            : s
                                        )
                                      );
                                    } else {
                                      // No alert for error
                                    }
                                  })
                                  .catch(error => {
                                    console.error('Error updating subject type:', error);
                                    // No alert for error
                                  });
                              }
                            }
                          }}
                        >
                          {subjectTypes.map(type => (
                            <option key={type.id_subject_type} value={type.id_subject_type}>
                              {type.type}
                            </option>
                          ))}
                        </select>
                      </td>
                      <td>
                        <button className="common-add-button" onClick={() => handleDeleteSubject(subject.id_subject, subject.subject)}>Remove</button>
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
          <div className="subjects-add">
            <input
              type="text"
              placeholder="Subject"
              value={newSubject}
              onChange={(e) => setNewSubject(e.target.value)}
            />
            <select
              value={selectedSubjectType !== null ? selectedSubjectType : ''}
              onChange={(e) => setSelectedSubjectType(Number(e.target.value))}
            >
              <option value="">Select a type</option>
              {subjectTypes.map(type => (
                <option key={type.id_subject_type} value={type.id_subject_type}>
                  {type.type}
                </option>
              ))}
            </select>
            <button
              onClick={() => {
                if (newSubject && selectedSubjectType !== null) {
                  const token = localStorage.getItem('jwtToken');
                  if (token) {
                    fetch(`${API_BASE_URL}/api/subjects`, {
                      method: 'POST',
                      headers: {
                        'Content-Type': 'application/json',
                        Authorization: `Bearer ${token}`,
                      },
                      body: JSON.stringify({
                        subject: newSubject,
                        id_subject_type: selectedSubjectType,
                      }),
                    })
                      .then(response => {
                        if (response.ok) {
                          // Fetch the updated list of subjects
                          fetch(`${API_BASE_URL}/api/subjects`, {
                            headers: {
                              Authorization: `Bearer ${token}`,
                            },
                          })
                            .then(response => response.json())
                            .then(subjectData => {
                              setSubjects(subjectData);
                              setNewSubject('');
                              setSelectedSubjectType(null);
                            })
                            .catch(error => {
                              console.error('Error fetching subjects:', error);
                            });
                        } else {
                          // No alert for error
                        }
                      })
                      .catch(error => {
                        console.error('Error adding subject:', error);
                        // No alert for error
                      });
                  }
                } else {
                  alert('Please fill in all required fields.');
                }
              }}
              disabled={!newSubject || selectedSubjectType === null}
            >
              Add Subject
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminManageSubjects;
