/**
 * Convertit une date ISO (ex : "2022-08-31T22:00:00.000Z")
 * vers le format "MM YY" (ex : "09 22")
 */
export function isoToMMYY(isoDate: string): string {
  const d = new Date(isoDate);
  const month = String(d.getMonth() + 1).padStart(2, "0");
  const year = String(d.getFullYear()).slice(-2);
  return `${month} ${year}`;
}

/**
 * Convertit un format "MM YY" vers ISO (ex : "09 22"),
 * en générant un ISO correspondant au 1er du mois
 * -> "2022-09-01T00:00:00.000Z"
 */
export function mmYYToISO(mmYY: string): string {
  const [month, year] = mmYY.split(" ");
  const fullYear = Number("20" + year);  // gère 22 → 2022
  const date = new Date(Date.UTC(fullYear, Number(month) - 1, 1));
  return date.toISOString();
}

/**
 * Convertit un objet Date en "MM YY"
 */
export function dateToMMYY(date: Date): string {
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const year = String(date.getFullYear()).slice(-2);
  return `${month} ${year}`;
}

/**
 * Convertit "MM YY" en objet Date (1er du mois)
 */
export function mmYYToDate(mmYY: string): Date {
  const [month, year] = mmYY.split(" ");
  return new Date(Number("20" + year), Number(month) - 1, 1);
}
