import React from 'react';
import { API_BASE_URL } from '../apiConfig';

interface SubjectType {
  id_subject_type: number;
  type: string;
  color_hex: string;
}

interface AdminManageSubjectTypesProps {
  subjectTypes: SubjectType[];
  setSubjectTypes: React.Dispatch<React.SetStateAction<SubjectType[]>>;
  newSubjectType: string;
  setNewSubjectType: React.Dispatch<React.SetStateAction<string>>;
}

const AdminManageSubjectTypes: React.FC<AdminManageSubjectTypesProps> = ({
  subjectTypes,
  setSubjectTypes,
  newSubjectType,
  setNewSubjectType
}) => {

  return (
    <div className="subject-types-section">
      <h2>Subject Types</h2>
      <div className="subject-types-content">
        <div className="subject-types-list">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Type</th>
                <th>Color</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {subjectTypes.map((type) => (
                <tr key={type.id_subject_type}>
                  <td>{type.id_subject_type}</td>
                  <td
                    contentEditable
                    suppressContentEditableWarning
                    onBlur={(e) => {
                      const newType = e.currentTarget.textContent?.trim() || '';
                      if (newType !== type.type) {
                        const token = localStorage.getItem('jwtToken');
                        if (token) {
                          fetch(`${API_BASE_URL}/api/subject-types/${type.id_subject_type}`, {
                            method: 'PUT',
                            headers: {
                              'Content-Type': 'application/json',
                              Authorization: `Bearer ${token}`,
                            },
                            body: JSON.stringify({ type: newType }),
                          })
                            .then(response => {
                              if (response.ok) {
                                // Update the subject type in the state
                                setSubjectTypes(prevTypes => prevTypes.map(t =>
                                  t.id_subject_type === type.id_subject_type
                                    ? { ...t, type: newType }
                                    : t
                                ));
                              } else {
                                // No alert for error
                              }
                            })
                            .catch(error => {
                              console.error('Error updating subject type:', error);
                              // No alert for error
                            });
                        }
                      }
                    }}
                  >
                    {type.type}
                  </td>
                  <td style={{ backgroundColor: type.color_hex, color: '#fff', textAlign: 'center', padding: '5px' }}>
                    {type.color_hex}
                  </td>
                  <td>
                    <button
                      className="common-add-button"
                      onClick={() => {
                        const isConfirmed = window.confirm(`Are you sure you want to delete the subject type "${type.type}"?`);
                        if (isConfirmed) {
                          const token = localStorage.getItem('jwtToken');
                          if (token) {
                            fetch(`${API_BASE_URL}/api/subject-types/${type.id_subject_type}`, {
                              method: 'DELETE',
                              headers: {
                                Authorization: `Bearer ${token}`,
                              },
                            })
                              .then(response => {
                                if (response.ok) {
                                  // Update the subject types in the state
                                  setSubjectTypes(prevTypes => prevTypes.filter(t => t.id_subject_type !== type.id_subject_type));
                                } else {
                                  // No alert for error
                                }
                              })
                              .catch(error => {
                                console.error('Error deleting subject type:', error);
                                // No alert for error
                              });
                          }
                        }
                      }}
                    >
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className="subject-types-add">
          <input
            type="text"
            placeholder="Type"
            value={newSubjectType}
            onChange={(e) => setNewSubjectType(e.target.value)}
          />
          <button
            onClick={() => {
              if (newSubjectType) {
                const token = localStorage.getItem('jwtToken');
                if (token) {
                  fetch(`${API_BASE_URL}/api/subject-types`, {
                    method: 'POST',
                    headers: {
                      'Content-Type': 'application/json',
                      Authorization: `Bearer ${token}`,
                    },
                    body: JSON.stringify({ type: newSubjectType }),
                  })
                    .then(response => {
                      if (response.ok) {
                        // Fetch the updated list of subject types
                        fetch(`${API_BASE_URL}/api/subject-types`, {
                          headers: {
                            Authorization: `Bearer ${token}`,
                          },
                        })
                          .then(response => response.json())
                          .then(typeData => {
                            const typesArray = typeData.map((type: any) => ({
                              id_subject_type: type.id_subject_type,
                              type: type.type,
                              color_hex: type.color_hex
                            }));
                            setSubjectTypes(typesArray);
                            setNewSubjectType('');
                          })
                          .catch(error => {
                            console.error('Error fetching subject types:', error);
                          });
                      } else {
                        // No alert for error
                      }
                    })
                    .catch(error => {
                      console.error('Error adding subject type:', error);
                      // No alert for error
                    });
                }
              } else {
                alert('Please fill in all required fields.');
              }
            }}
            disabled={!newSubjectType}
          >
            Add Type
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdminManageSubjectTypes;
